<?php

namespace AppBundle\Repository;

/**
 * ResultExerciseRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ResultExerciseRepository extends \Doctrine\ORM\EntityRepository
{
    public function getAllResultExercisesByUser($user)
    {
        return $this->createQueryBuilder('re')
                ->leftJoin('re.result', 'r')
                ->leftJoin('r.pack', 'p')
                ->leftJoin('p.initial', 'i')
                ->leftJoin('re.exercise', 'e')
                ->where('i.id = :id')
                ->andWhere('e.reference = :reference')
                ->andWhere('re.repetition IS NOT NULL')
                ->setParameter('id', $user->getId())
                ->setParameter('reference', true)
                ->getQuery()
                ->getResult()
            ;
    }
}
